mapper.select:
select save_folder, original_file, save_file
from file_info
where board_no = #{board_no}

mapper.update:
update board
set board_hit = board_hit + 1
where board_no = ${board_no}

update board
set board_title = #{board_title}, board_content = #{board_content}
where board_no = ${board_no}
